mapper.select:
select count(id) from wy_equipment
where
del_flag = '0'

        AND company = #{company}


        AND name like "%"#{name}"%"
        or code = #{name}

select * from wy_equipment
where
del_flag = '0'

    AND company = #{company}


    AND name like "%"#{name}"%"
    or code = #{name}

SELECT count(1) FROM ( SELECT * FROM wy_equipment) a LEFT JOIN ( SELECT * FROM wy_maintainorder) b ON a.id = b.workid LEFT JOIN ( SELECT * FROM wy_equipmentstrategy) c ON b.id = c.policyid WHERE 1=1
AND a.name like #{Name}
AND a.code like #{Code}
AND a.department in #{Department}

SELECT a.id, a.name, a.code, a.model, a.parameter, a.status, a.location, a.manufacturer, a.production, a.period, a.servicelife, a.period, a.company, a.del_flag FROM ( SELECT * FROM wy_equipment) a WHERE 1=1
AND a.name like #{Name} OR a.code like #{Name} OR a.Department like #{Name}
AND del_flag = 0

SELECT count(1) FROM ( SELECT * FROM wy_equipment) a LEFT JOIN ( SELECT * FROM wy_maintainorder) b ON a.id = b.workid LEFT JOIN ( SELECT * FROM wy_equipmentstrategy) c ON b.id = c.policyid WHERE 1=1
AND a.id = #{Id}
AND del_flag = 0

SELECT b.id, b.workno, d.paravalue workstate, b.personnel, b.receiving, b.defectidentification, b.completiontime, b.operationrecord, b.workid, c.policyname, c.policy FROM (SELECT * FROM wy_equipment) a LEFT JOIN ( SELECT * FROM wy_maintainorder) b ON a.id = b.workid LEFT JOIN ( SELECT * FROM wy_equipmentstrategy) c ON b.id = c.policyid LEFT JOIN (select * from sys_parameter where paraname = 'workstate') d on b.workstate = d.parakey WHERE 1=1
AND a.id = #{Id}
limit #{startIndex},#{pageSize}
